const conditions = [
  { label: 'ÏïÑÏ£º Ï¢ãÏùå', english: 'well', emoji: 'ü§©' }, 
  { label: 'Ï¢ãÏùå', english: 'good', emoji: 'üòä' }, 
  { label: 'ÌîºÍ≥§Ìï®', english: 'tired', emoji: 'üò¥' }, 
  { label: 'Îß§Ïö∞ ÌîºÍ≥§Ìï®', english: 'exhausted', emoji: 'üò©' }, 
  { label: 'ÏÜåÌôîÎ∂àÎüâ', english: 'indigestion', emoji: 'ü§¢' }, 
  { label: 'ÎëêÌÜµ', english: 'headache', emoji: 'ü§ï' }, 
  { label: 'Î™∏ÏÇ¥', english: 'cold', emoji: 'ü§í' }, 
  { label: 'Í∞êÍ∏∞', english: 'flu', emoji: 'ü§ß' }, 
];

function EmotionInput() {
  const navigate = useNavigate();
  const [selectedCondition, setSelectedCondition] = useState(null);

  const handleConditionSelect = (condition) => {
    setSelectedCondition(condition);
    console.log('ÏÑ†ÌÉùÌï®:', condition.label);
  };

  const handleNextClick = () => {
    if (selectedCondition) {
      console.log('Îã§ÏùåÏúºÎ°ú Ïù¥Îèô!');

      navigate('/condition', { state: { condition: selectedCondition } });

    } else {
      alert('Ïò§ÎäòÏùò Ïª®ÎîîÏÖòÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  return (
    <div className="emotion-input-container">
      <h2>Ïò§ÎäòÏùò Ïª®ÎîîÏÖòÏùÄ Ïñ¥Îñ†Ïã†Í∞ÄÏöî?</h2>

      <div className="emotion-options">
        {conditions.map((condition, index) => (
          <div 
            key={index} 
            className={`emotion-option ${selectedCondition?.label === condition.label ? 'selected' : ''}`}
            onClick={() => handleConditionSelect(condition)}
          >
            <span className="emoji">{condition.emoji}</span>
            <span className="label">{condition.label}</span>
          </div>
        ))}
      </div>

      <button className="next-button" onClick={handleNextClick}>
        Îã§Ïùå
      </button>
    </div>
  );
}

export default EmotionInput;
